mapper.update:
update t_teacher_subject


         username = #{username},


         title = #{title},


         detail = #{detail},


        type = #{type}


where id = #{id}

mapper.select:
select departId from t_teacher tt
  left join t_teacher_subject tts on tts.username = tt.username
  where tts.id = #{id}

select ifnull(count(*),0) from t_task where departId = #{departId}
  and unix_timestamp(endTime) > unix_timestamp(#{time})
  and unix_timestamp(startTime) < unix_timestamp(#{time})
