mapper.select:
select ifnull(count(*),0) from t_student_subject tss
  left join t_teacher_subject tts on tts.id = tss.subjectId
  where tss.username = #{username} and tts.username = #{StuUsername}

mapper.update:
update t_teacher_file


         url = #{url}


        and createTime = #{createTime}


where username = #{username } and name = #{name}
